import React,{ useState } from 'react';
import { Container,Row,Col,Button } from 'react-bootstrap';
import{Card,Modal} from 'react-bootstrap';
import EditContactForm from './EditContactForm';

const ContactA = (props) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleDelete = (e) =>{
    e.preventDefault();
   props.deleteUser(props.AllContactData.id);
  }


    return (

        <>
        <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Edit User</Modal.Title>
        </Modal.Header> 
        <Modal.Body>
          <EditContactForm UserContact={props.UserContact} editUser={props.editUser} closeModal={handleClose}/>
          
        </Modal.Body>
       
      </Modal>
          <Container>
            <Row>
                <Col md={6} style={{ marginBottom: "1rem" }}>
                <Card style={{ width: '18rem' , margin: '20px' ,border:'1px solid dodgerBlue'}}>
      <Card.Body>
        <Card.Title>Name: {props.AllContactData.name}</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">Phone: {props.AllContactData.phone}</Card.Subtitle>
        <Card.Text>
         Location: {props.AllContactData.location}
        </Card.Text>
        <Button href="#" variant='success'  onClick={handleShow}>Edit</Button>
        <Button href="#" variant='danger' onClick={handleDelete}>Delete</Button>
      </Card.Body>
    </Card></Col>
            </Row>
            </Container>  
        </>
    );
}

export default ContactA;
