import React, {Component} from "react"
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap';
import AllContact from './components/AllContact';
import AddContactForm from "./components/AddContactForm";



class App extends Component{
  constructor(props){
    super(props)
    this.state={
      AllUsers: [
        {
          name: "constance",
          email: "const@gmail.com",
          gen: "21",
        },
        {
          name: "confidence",
          email: "confy@gmail.com",
          gen: "22",
        },
        {
          name: "comfort",
          email: "comfy@gmail.com",
          gen: "23",
        },
        // {
        //   name: "constance",
        //   phone: "+233 956 789 099",
        //   location: "Accra",
        // },
        // {
        //   name: "confidence",
        //   phone: "+234 956 789 099",
        //   location: "Kumasi",
        // },
        // {
        //   name: "comfort",
        //   phone: "+235 956 789 099",
        //   location: "Volta",
        // },
      ]
    }
  }
  addNewContact = (user) => {
    this.setState({
      AllUsers: [
        ...this.state.AllUsers, user
      ]
    })
  }
  render(){
  return (
    <>
    <Container fluid style={{marginTop: "2rem"}}>
      <Row>
        <Col md="4">
          <AddContactForm addUser={this.addNewContact}/>
        </Col>
      <Col>
        <AllContact AllUserData={this.state.AllUsers} /> 
      </Col>
      </Row>
    </Container>
    </>
  );
}
}

export default App;
