import React, {useState} from "react";
import {Form, Button } from "react-bootstrap";
function EditContactForm (props) {
const [name, setName] = useState(props.contactBio.name);
const [phone, setPhone] = useState(props.contactBio.phone);
const [location, setLocation] = useState(props.contactBio.location);

const handleSubmit = (e) => {
    e.preventDefault();
    props.editContact(props.contactBio.id,{name, phone, location});
    setName("");
    setPhone("");
    setLocation("");
};
return (
    <>
            <Form onSubmit={this.handleSubmit}>
      <Form.Group className="mb-3" controlId="formBasicName">
        <Form.Label>Name:</Form.Label>
        <Form.Control type="text" placeholder="Enter name" name="name" 
        value={name}
        onChange={(e) => {
            setName(e.target.value);
        }}/>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPhone">
        <Form.Label>Phone:</Form.Label>
        <Form.Control type="tel" placeholder="Telephone Number" name="phone"
         value={phone}
         onChange={(e) => {
             setPhone(e.target.value);
         }}/>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicLocation">
        <Form.Label>Location:</Form.Label>
        <Form.Control type="location" placeholder="Location" name="location" 
        value={location}
        onChange={(e) => {
            setLocation(e.target.value);
        }} />
      </Form.Group>
      <Button onClick={handleSubmit} variant="primary" type="submit">
        Submit
      </Button>
    </Form>
    </>
);
}
export default EditContactForm;
