import React, {useState} from 'react';
import Card from 'react-bootstrap/Card';
import {Col} from 'react-bootstrap';
import {Button, Modal} from 'react-bootstrap';
import EditContactForm from '../EditContactForm';

function ContactA (props) {
    const [show, setShow] = useState(false);
	const handleClose = () => setShow(false);
	const handleShow = () => setShow(true);

	const hadleDelete = (e) => {
		e.preventDefault();
		props.delete(props.contactBio.id);
	};

    <>
			<Modal show={show} onHide={handleClose}>
				<Modal.Header closeButton>
					<Modal.Title>Contact Form</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					<EditContactForm editContact={props.editContact} contactBio={props.contactBio} />
				</Modal.Body>
			</Modal>
			<Col md={4} style={{ width: "18rem", marginBottom: "1rem" }}>
				<Card>
					<Card.Body>
						<Card.Title>Name:{props.AllContactInfo.name}</Card.Title>
						<Card.Subtitle className="mb-2 text-muted">
							Phone:{props.AllContactInfo.phone}
						</Card.Subtitle>
						<Card.Text>Location:{props.AllContactInfo.location}</Card.Text>
						<Button href="#" size="sm" variant="success" onClick={handleShow}>
							Edit
						</Button>
						<Button href="#" size="sm" variant="danger" onClick={hadleDelete}>
							Delete
						</Button>
					</Card.Body>
				</Card>
			</Col>
		</>
}
