import React, {Component} from "react"
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap';
import AllContact from './components/AllContact';
import AddContactForm from "./components/AddContactForm";



class App extends Component{
  constructor(props){
    super(props)
    this.state={
      AllContacts: [
        {
          name: "constance",
          phone: "+233 956 789 099",
          location: "Accra",
        },
        {
          name: "confidence",
          phone: "+234 956 789 099",
          location: "Kumasi",
        },
        {
          name: "comfort",
          phone: "+235 956 789 099",
          location: "Volta",
        },
      ]
    }
  }
  addNewContact = (Contact) => {
    this.setState({
      AllContacts: [
        ...this.state.AllContacts, Contact
      ]
    })
  }
  render(){
  return (
    <>
    <Container fluid style={{marginTop: "2rem"}}>
      <Row>
        <Col md="4">
          <AddContactForm addContact={this.addNewContact}/>
        </Col>
      <Col>
        <AllContact AllContactData={this.state.AllContacts} /> 
      </Col>
      </Row>
    </Container>
    </>
  );
}
}

export default App;
