import React from 'react';
import Card from 'react-bootstrap/Card';
import {Col} from 'react-bootstrap';
import {Button} from 'react-bootstrap'

const ContactA = (props) => {
    return (
        <>
            <Col md="4" style={{marginBottom: "1rem"}}>
                        <Card style={{ width: '18rem' }}>
                <Card.Body>
                    <Card.Title>Codtrain User</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted">{props.AllContactInfo.name}</Card.Subtitle>
                    <Card.Text>
                    <p>Phone:{props.AllContactInfo.phone}</p>
                    <p>Location: {props.AllContactInfo.location}</p>
                    </Card.Text>
                    <Button style={{marginLeft: "1rem"}} variant="success" href="#">Edit</Button>
                    <Button style={{marginLeft: "1rem"}} variant="danger" href="#">Delete</Button>
                </Card.Body>
                </Card>
            </Col>

            
            
            
            
        </>    
    );
}

export default ContactA;

